package _07_conditionals;

public class _02_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean meatloafForLunch = true;
		boolean salad = false;
		boolean coffee = true;
		boolean pizza = false;
		
		if (meatloafForLunch) {
			System.out.println("I will be happy.");
		} else {
			System.out.println("Maybe tomorrow.");
		}

		if (salad) {
				System.out.println("This should help my diet.");
		} else {
				System.out.println("Don't substitute with something else.");
			}
		
		if (coffee) {
				System.out.println("This should help me stay awake.");
		} else {
				System.out.println("Take a nap after lunch.");
			}
		
		if (pizza) {
				System.out.println("This is my favorite food.");
		} else {
				System.out.println("A day without pizza is a day without sunshine.");
			}
		}
		
	}

	
